export interface IFeature {
    name: string;
    value: any;
}
export interface IFeatureService {
    getApplicationIdsByFeatures(features: Array<IFeature>): Array<string>;
    getRoutingAdapterKeyByApplicationId(applicationId: string): string;
    addFeature(featureName: string, value: any): void;
    hasFeature(featureName: string, value?: any, applicationId?: string): boolean;
    hasFeatures(features: Array<IFeature>, applicationId?: string): boolean;
    mergeFeatures(...args: Array<Array<IFeature>>): Array<IFeature>;
    initialize(): void;
}
export interface IFeatureGenerator {
    generate(): Promise<Array<IFeature>>;
}
