import {ExecutionContext} from '@process-engine-js/core_contracts';

export enum InvocationType {
  before,
  after
}

export interface IInvoker {
  setInvocationForType(type: string, property: string, invocationType: InvocationType, func: Function): void;
  setInvocationForInstance(instance: Object, property: string, invocationType: InvocationType, func: Function): void;
  invoke(instance: any, property: string, namespace: string, context: ExecutionContext, ...restArgs: Array<any>): Promise<any>
}