import * as uuid from 'uuid';
import { EntityReference } from '@process-engine-js/data_model_contracts';
import { IMessage, IMessageMetadata } from './interfaces';

export class Message implements IMessage {
  id: string;
  data: any;
  metadata: IMessageMetadata;

  constructor(data: any, metadata: IMessageMetadata) {
    this.id = uuid.v4();
    this.data = data;
    this.metadata = metadata;
  }
}